import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { authService } from '../services/api';

const Login = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    userType: 'customer'
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    setIsLoading(true);
    setMessage('');

    try {
      const response = await authService.login(formData.email, formData.password, formData.userType);

      if (response.user) {
        // Store token if provided
        if (response.token) {
          localStorage.setItem('authToken', response.token);
        }
        
        login(response.user);
        setMessage('Login successful!');
        
        setTimeout(() => {
          if (response.user.userType === 'admin') {
            navigate('/admin');
          } else {
            navigate('/');
          }
        }, 1000);
      }
    } catch (error) {
      console.error('Login error:', error);
      setMessage('Invalid email or password. Please register if you don\'t have an account.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="card" style={{ maxWidth: '500px', margin: '2rem auto' }}>
        <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
          <h2>üéì Welcome Back to Snuggle Read</h2>
          <p>Sign in to your student account</p>
        </div>
        
        {message && (
          <div className={`alert ${message.includes('Invalid') ? 'alert-error' : 'alert-success'}`}>
            {message}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="userType">Account Type:</label>
            <select
              id="userType"
              name="userType"
              value={formData.userType}
              onChange={handleChange}
            >
              <option value="customer">Student Account</option>
              <option value="admin">Admin Account</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="email">Email Address:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your university email"
            />
            {errors.email && <div className="error">{errors.email}</div>}
          </div>

          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Enter your password"
            />
            {errors.password && <div className="error">{errors.password}</div>}
          </div>

          <button 
            type="submit" 
            className="btn"
            disabled={isLoading}
            style={{ width: '100%', marginBottom: '1rem' }}
          >
            {isLoading ? 'Signing In...' : 'üîê Sign In'}
          </button>
        </form>

        <div style={{ textAlign: 'center', marginTop: '1.5rem' }}>
          <p>Don't have an account? 
            <Link to="/register" style={{ marginLeft: '0.5rem', color: '#2a5298', textDecoration: 'none', fontWeight: 'bold' }}>
              Create Student Account
            </Link>
          </p>
        </div>

        {/* Demo Credentials */}
        <div className="card" style={{ 
          marginTop: '2rem', 
          background: 'linear-gradient(135deg, #e3f2fd, #f3e5f5)',
          border: '2px dashed #2a5298'
        }}>
          <h4 style={{ marginBottom: '1rem', color: '#2a5298' }}>üß™ Demo Credentials</h4>
          <div style={{ fontSize: '0.9rem', lineHeight: '1.6' }}>
            <strong>Admin Account:</strong><br />
            Email: admin@adp.ac.za<br />
            Password: admin123<br /><br />
            
            <strong>Student Accounts:</strong><br />
            Email: student@uct.ac.za<br />
            Password: student123<br /><br />
            
            Email: aimeep@gmail.com<br />
            Password: aimeep
          </div>
        </div>

        <div style={{ textAlign: 'center', marginTop: '1.5rem', fontSize: '0.9rem', color: '#7f8c8d' }}>
          <p>üîí Your data is secure with Snuggle Read</p>
          <p>üìö Access thousands of academic resources</p>
        </div>
      </div>
    </div>
  );
};

export default Login;
