import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { authService, handleAPIError } from '../services/api';

const Login = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const { isAdmin } = useAuth();
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    userType: 'customer'
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;

    setIsLoading(true);
    setMessage('');

    try {
      const response = await authService.login(formData.email, formData.password, formData.userType);

      // response may be { user, token } or a raw user object (backend returns User)
      const userObj = response.user ? response.user : response;
      // Store token if provided
      if (response.token) {
        localStorage.setItem('authToken', response.token);
      }

      if (userObj) {
        login(userObj);
        setMessage('Login successful!');

        setTimeout(() => {
          if (isAdmin()) {
            navigate('/admin');
          } else {
            navigate('/');
          }
        }, 1000);
      }
    } catch (error) {
      console.error('Login error (raw):', error);
      // Use shared API error formatter to get a helpful message for the user
      // If backend returned 401, suggest registering first
      if (error.response && error.response.status === 401) {
        setMessage('Invalid credentials or account not found. Please register first.');
      } else {
        const friendly = handleAPIError(error);
        setMessage(friendly);
      }
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="card" style={{ maxWidth: '500px', margin: '2rem auto' }}>
        <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
          <h2>üéì Welcome Back to Snuggle Read</h2>
          <p>Sign in to your student account</p>
        </div>
        
        {message && (
          <div className={`alert ${message.includes('Invalid') ? 'alert-error' : 'alert-success'}`}>
            {message}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          {/* Account type is fixed to 'customer' for UI login; admin accounts are provisioned server-side */}

          <div className="form-group">
            <label htmlFor="email">Email Address:</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your university email"
            />
            {errors.email && <div className="error">{errors.email}</div>}
          </div>

          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Enter your password"
            />
            {errors.password && <div className="error">{errors.password}</div>}
          </div>

          <button 
            type="submit" 
            className="btn"
            disabled={isLoading}
            style={{ width: '100%', marginBottom: '1rem' }}
          >
            {isLoading ? 'Signing In...' : 'üîê Sign In'}
          </button>
        </form>

        <div style={{ textAlign: 'center', marginTop: '1.5rem' }}>
          <p>Don't have an account? 
            <Link to="/register" style={{ marginLeft: '0.5rem', color: '#2a5298', textDecoration: 'none', fontWeight: 'bold' }}>
              Create Student Account
            </Link>
          </p>
        </div>

        <div style={{ textAlign: 'center', marginTop: '1.5rem', fontSize: '0.9rem', color: '#7f8c8d' }}>
          <p>üîí Your data is secure with Snuggle Read</p>
          <p>üìö Access thousands of academic resources</p>
        </div>
      </div>
    </div>
  );
};

export default Login;
