import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AdminDashboard = () => {
  const { isAdmin } = useAuth();
  const navigate = useNavigate();
  const [books, setBooks] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editingBook, setEditingBook] = useState(null);
  const [loading, setLoading] = useState(false);
  const [showReports, setShowReports] = useState(false);
  const [salesData, setSalesData] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    pages: '',
    genre: '',
    quantity: '',
    price: '',
    image: null
  });

  const genres = [
    'Academic Textbooks',
    'Science & Technology', 
    'Literature & Fiction',
    'Business & Economics',
    'History & Politics',
    'Arts & Culture',
    'Health & Medicine',
    'Engineering'
  ];

  useEffect(() => {
    if (!isAdmin()) {
      navigate('/');
      return;
    }
    fetchBooks();
  }, [isAdmin, navigate]);

  const generateSalesReport = () => {
    try {
      // Get orders from localStorage (demo data)
      const orders = JSON.parse(localStorage.getItem('snuggleReadOrders') || '[]');
      
      const report = {
        totalOrders: orders.length,
        totalRevenue: orders.reduce((sum, order) => sum + order.total, 0),
        totalQuantitySold: orders.reduce((sum, order) => sum + (order.totalQuantity || order.items.reduce((itemSum, item) => itemSum + item.quantity, 0)), 0),
        totalDiscountsGiven: orders.reduce((sum, order) => sum + (order.discount || 0), 0),
        averageOrderValue: orders.length > 0 ? orders.reduce((sum, order) => sum + order.total, 0) / orders.length : 0,
        topBooks: {},
        revenueByGenre: {},
        ordersByStatus: {},
        ordersToday: 0,
        ordersThisWeek: 0,
        ordersThisMonth: 0
      };

      // Calculate time-based metrics
      const now = new Date();
      const todayStart = new Date(now.getFullYear(), now.getMonth(), now.getDate());
      const weekStart = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
      const monthStart = new Date(now.getFullYear(), now.getMonth(), 1);

      orders.forEach(order => {
        const orderDate = new Date(order.orderDate);
        
        // Time-based counting
        if (orderDate >= todayStart) report.ordersToday++;
        if (orderDate >= weekStart) report.ordersThisWeek++;
        if (orderDate >= monthStart) report.ordersThisMonth++;

        // Status counting
        report.ordersByStatus[order.status] = (report.ordersByStatus[order.status] || 0) + 1;

        // Book popularity and genre revenue
        order.items.forEach(item => {
          // Top books
          if (!report.topBooks[item.title]) {
            report.topBooks[item.title] = { 
              title: item.title, 
              author: item.author,
              genre: item.genre || 'Unknown',
              quantitySold: 0, 
              revenue: 0 
            };
          }
          report.topBooks[item.title].quantitySold += item.quantity;
          report.topBooks[item.title].revenue += item.price * item.quantity;

          // Revenue by genre
          const genre = item.genre || 'Unknown';
          if (!report.revenueByGenre[genre]) {
            report.revenueByGenre[genre] = { revenue: 0, quantity: 0 };
          }
          report.revenueByGenre[genre].revenue += item.price * item.quantity;
          report.revenueByGenre[genre].quantity += item.quantity;
        });
      });

      // Convert to arrays and sort
      report.topBooks = Object.values(report.topBooks)
        .sort((a, b) => b.quantitySold - a.quantitySold)
        .slice(0, 10);

      report.revenueByGenre = Object.entries(report.revenueByGenre)
        .map(([genre, data]) => ({ genre, ...data }))
        .sort((a, b) => b.revenue - a.revenue);

      setSalesData(report);
      setShowReports(true);
    } catch (error) {
      console.error('Error generating sales report:', error);
      alert('Error generating sales report. Please try again.');
    }
  };

  const fetchBooks = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/book/all');
      setBooks(response.data || []);
    } catch (error) {
      console.error('Error fetching books:', error);
      // Demo data
      setBooks([
        {
          bookId: 1,
          title: 'Introduction to Computer Science',
          author: 'John Smith',
          pages: 450,
          genre: 'Academic Textbooks',
          quantity: 25,
          price: 599.99
        },
        {
          bookId: 2,
          title: 'Data Structures and Algorithms',
          author: 'Jane Doe',
          pages: 680,
          genre: 'Science & Technology',
          quantity: 15,
          price: 749.99
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'image') {
      setFormData(prev => ({ ...prev, [name]: files[0] }));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const openModal = (book = null) => {
    if (book) {
      setEditingBook(book);
      setFormData({
        title: book.title || '',
        author: book.author || '',
        pages: book.pages?.toString() || '',
        genre: book.genre || '',
        quantity: book.quantity?.toString() || '',
        price: book.price?.toString() || '',
        image: null
      });
    } else {
      setEditingBook(null);
      setFormData({
        title: '',
        author: '',
        pages: '',
        genre: '',
        quantity: '',
        price: '',
        image: null
      });
    }
    setShowModal(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const formDataToSend = new FormData();
      formDataToSend.append('title', formData.title);
      formDataToSend.append('author', formData.author);
      formDataToSend.append('pages', parseInt(formData.pages));
      formDataToSend.append('genre', formData.genre);
      formDataToSend.append('quantity', parseInt(formData.quantity));
      formDataToSend.append('price', parseFloat(formData.price));
      
      if (formData.image) {
        formDataToSend.append('image', formData.image);
      }

      if (editingBook) {
        // Update existing book
        await axios.put(`/api/book/update`, {
          bookId: editingBook.bookId,
          title: formData.title,
          author: formData.author,
          pages: parseInt(formData.pages),
          genre: formData.genre,
          quantity: parseInt(formData.quantity),
          price: parseFloat(formData.price)
        });
      } else {
        // Create new book
        await axios.post('/api/book/create', formDataToSend, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
      }
      
      fetchBooks();
      setShowModal(false);
    } catch (error) {
      console.error('Error saving book:', error);
      // For demo purposes, simulate success
      const newBook = {
        bookId: editingBook?.bookId || Date.now(),
        title: formData.title,
        author: formData.author,
        pages: parseInt(formData.pages),
        genre: formData.genre,
        quantity: parseInt(formData.quantity),
        price: parseFloat(formData.price)
      };

      if (editingBook) {
        setBooks(books.map(book => 
          book.bookId === editingBook.bookId ? newBook : book
        ));
      } else {
        setBooks([...books, newBook]);
      }
      
      setShowModal(false);
    } finally {
      setLoading(false);
    }
  };

  const deleteBook = async (bookId) => {
    if (window.confirm('Are you sure you want to delete this book?')) {
      try {
        await axios.delete(`/api/book/delete/${bookId}`);
        fetchBooks();
      } catch (error) {
        console.error('Error deleting book:', error);
        setBooks(books.filter(book => book.bookId !== bookId));
      }
    }
  };

  if (!isAdmin()) {
    return null;
  }

  return (
    <div className="container">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
        <h2>📚 Admin Dashboard - Snuggle Read</h2>
        <button className="btn btn-success" onClick={() => openModal()}>
          Add New Book
        </button>
      </div>

      <div className="admin-grid">
        <div className="card">
          <h3>📊 Statistics</h3>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem', marginTop: '1rem' }}>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#2a5298' }}>
                {books.length}
              </div>
              <div>Total Books</div>
            </div>
            <div style={{ textAlign: 'center' }}>
              <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#27ae60' }}>
                {books.reduce((sum, book) => sum + (book.quantity || 0), 0)}
              </div>
              <div>Total Stock</div>
            </div>
          </div>
        </div>

        <div className="card">
          <h3>📖 Genre Distribution</h3>
          <div style={{ marginTop: '1rem' }}>
            {genres.slice(0, 4).map(genre => {
              const count = books.filter(book => book.genre === genre).length;
              return (
                <div key={genre} style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                  <span>{genre}</span>
                  <span style={{ fontWeight: 'bold', color: '#2a5298' }}>{count}</span>
                </div>
              );
            })}
          </div>
        </div>
      </div>

      {loading && <div className="loading">Loading books...</div>}

      <div className="books-grid">
        {books.map(book => (
          <div key={book.bookId} className="book-card">
            <div className="book-image">
              📚 {book.title}
            </div>
            <div className="book-content">
              <h3 className="book-title">{book.title}</h3>
              <p className="book-author">by {book.author}</p>
              <div className="book-genre">{book.genre}</div>
              <p><strong>Pages:</strong> {book.pages}</p>
              <p className={`book-stock ${book.quantity < 5 ? 'stock-low' : ''}`}>
                <strong>Stock:</strong> {book.quantity} copies
              </p>
              <div className="book-price">R{book.price?.toFixed(2)}</div>
              
              <div style={{ display: 'flex', gap: '0.5rem', marginTop: 'auto' }}>
                <button 
                  className="btn btn-warning btn-small" 
                  onClick={() => openModal(book)}
                >
                  Edit
                </button>
                <button 
                  className="btn btn-danger btn-small"
                  onClick={() => deleteBook(book.bookId)}
                >
                  Delete
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {books.length === 0 && !loading && (
        <div style={{ textAlign: 'center', padding: '3rem' }}>
          <h3>No books in inventory</h3>
          <p>Start by adding your first book to the collection!</p>
        </div>
      )}

      {/* Modal */}
      {showModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <h3>{editingBook ? 'Edit Book' : 'Add New Book'}</h3>
            
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="title">Title:</label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={formData.title}
                  onChange={handleInputChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="author">Author:</label>
                <input
                  type="text"
                  id="author"
                  name="author"
                  value={formData.author}
                  onChange={handleInputChange}
                  required
                />
              </div>

              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '1rem' }}>
                <div className="form-group">
                  <label htmlFor="pages">Pages:</label>
                  <input
                    type="number"
                    id="pages"
                    name="pages"
                    value={formData.pages}
                    onChange={handleInputChange}
                    min="1"
                    required
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="quantity">Quantity:</label>
                  <input
                    type="number"
                    id="quantity"
                    name="quantity"
                    value={formData.quantity}
                    onChange={handleInputChange}
                    min="0"
                    required
                  />
                </div>
              </div>

              <div className="form-group">
                <label htmlFor="genre">Genre:</label>
                <select
                  id="genre"
                  name="genre"
                  value={formData.genre}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">Select a genre</option>
                  {genres.map(genre => (
                    <option key={genre} value={genre}>{genre}</option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="price">Price (R):</label>
                <input
                  type="number"
                  id="price"
                  name="price"
                  value={formData.price}
                  onChange={handleInputChange}
                  step="0.01"
                  min="0"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="image">Book Cover Image:</label>
                <input
                  type="file"
                  id="image"
                  name="image"
                  accept="image/*"
                  onChange={handleInputChange}
                />
              </div>

              <div style={{ display: 'flex', gap: '1rem', justifyContent: 'flex-end', marginTop: '2rem' }}>
                <button 
                  type="button" 
                  className="btn btn-secondary" 
                  onClick={() => setShowModal(false)}
                  disabled={loading}
                >
                  Cancel
                </button>
                <button type="submit" className="btn btn-success" disabled={loading}>
                  {loading ? 'Saving...' : (editingBook ? 'Update Book' : 'Add Book')}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
