import axios from 'axios';

// Base URL configuration
const API_BASE_URL = '/bookstore/api';

// Create axios instance with default config
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token if available
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('authToken');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Response interceptor for error handling
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Handle unauthorized access
      localStorage.removeItem('authToken');
      localStorage.removeItem('userInfo');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Authentication services
export const authService = {
  login: async (email, password, userType = 'customer') => {
    try {
      // Check for hardcoded admin credentials (security requirement)
      if (email === 'admin@adp.ac.za' && password === 'admin123') {
        return {
          user: {
            userId: 'admin1',
            userFirstName: 'Admin',
            userLastName: 'User',
            userEmail: 'admin@adp.ac.za',
            userType: 'admin'
          },
          token: 'admin-token-' + Date.now()
        };
      }
      
      // For regular users, call backend API
      const endpoint = userType === 'admin' ? '/admin/login' : '/customer/login';
      const response = await api.post(endpoint, { email, password });
      return response.data;
    } catch (error) {
      throw error;
    }
  },

  logout: () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('userInfo');
  },

  getCurrentUser: () => {
    const userInfo = localStorage.getItem('userInfo');
    return userInfo ? JSON.parse(userInfo) : null;
  },

  setUser: (user, token) => {
    localStorage.setItem('userInfo', JSON.stringify(user));
    if (token) {
      localStorage.setItem('authToken', token);
    }
  }
};

// User services
export const userService = {
  getAll: () => api.get('/users/getall'),
  getById: (id) => api.get(`/users/read/${id}`),
  create: (userData) => api.post('/users/create', userData),
  update: (id, userData) => api.put(`/users/update/${id}`, userData),
  delete: (id) => api.delete(`/users/delete/${id}`),
};

// Book services
export const bookService = {
  getAll: () => api.get('/books/getall'),
  getById: (id) => api.get(`/books/read/${id}`),
  create: (bookData) => api.post('/books/create', bookData),
  update: (id, bookData) => api.put(`/books/update/${id}`, bookData),
  delete: (id) => api.delete(`/books/delete/${id}`),
  searchByTitle: (title) => api.get(`/books/search/title/${title}`),
  searchByAuthor: (author) => api.get(`/books/search/author/${author}`),
  searchByGenre: (genre) => api.get(`/books/search/genre/${genre}`),
};

// Order services
export const orderService = {
  getAll: () => api.get('/orders/getall'),
  getById: (id) => api.get(`/orders/read/${id}`),
  getByUserId: (userId) => api.get(`/orders/user/${userId}`),
  create: (orderData) => api.post('/orders/create', orderData),
  update: (id, orderData) => api.put(`/orders/update/${id}`, orderData),
  delete: (id) => api.delete(`/orders/delete/${id}`),
};

// Order Item services
export const orderItemService = {
  getAll: () => api.get('/orderitems/getall'),
  getById: (id) => api.get(`/orderitems/read/${id}`),
  getByOrderId: (orderId) => api.get(`/orderitems/order/${orderId}`),
  create: (orderItemData) => api.post('/orderitems/create', orderItemData),
  update: (id, orderItemData) => api.put(`/orderitems/update/${id}`, orderItemData),
  delete: (id) => api.delete(`/orderitems/delete/${id}`),
};

// Payment services
export const paymentService = {
  getAll: () => api.get('/payments/getall'),
  getById: (id) => api.get(`/payments/read/${id}`),
  getByOrderId: (orderId) => api.get(`/payments/order/${orderId}`),
  create: (paymentData) => api.post('/payments/create', paymentData),
  update: (id, paymentData) => api.put(`/payments/update/${id}`, paymentData),
  delete: (id) => api.delete(`/payments/delete/${id}`),
};

// Contact services
export const contactService = {
  getAll: () => api.get('/contacts/getall'),
  getById: (id) => api.get(`/contacts/read/${id}`),
  create: (contactData) => api.post('/contacts/create', contactData),
  update: (id, contactData) => api.put(`/contacts/update/${id}`, contactData),
  delete: (id) => api.delete(`/contacts/delete/${id}`),
};

// Supplier services
export const supplierService = {
  getAll: () => api.get('/suppliers/getall'),
  getById: (id) => api.get(`/suppliers/read/${id}`),
  create: (supplierData) => api.post('/suppliers/create', supplierData),
  update: (id, supplierData) => api.put(`/suppliers/update/${id}`, supplierData),
  delete: (id) => api.delete(`/suppliers/delete/${id}`),
};

// Supply Order services
export const supplyOrderService = {
  getAll: () => api.get('/supplyorders/getall'),
  getById: (id) => api.get(`/supplyorders/read/${id}`),
  create: (supplyOrderData) => api.post('/supplyorders/create', supplyOrderData),
  update: (id, supplyOrderData) => api.put(`/supplyorders/update/${id}`, supplyOrderData),
  delete: (id) => api.delete(`/supplyorders/delete/${id}`),
};

// Supply Line services
export const supplyLineService = {
  getAll: () => api.get('/supplylines/getall'),
  getById: (id) => api.get(`/supplylines/read/${id}`),
  create: (supplyLineData) => api.post('/supplylines/create', supplyLineData),
  update: (id, supplyLineData) => api.put(`/supplylines/update/${id}`, supplyLineData),
  delete: (id) => api.delete(`/supplylines/delete/${id}`),
};

// Error handler utility
export const handleAPIError = (error) => {
  if (error.code === 'ECONNREFUSED') {
    return 'Unable to connect to server. Please check if the backend is running.';
  } else if (error.response?.status === 400) {
    return error.response.data?.message || 'Invalid request. Please check your input.';
  } else if (error.response?.status === 401) {
    return 'Unauthorized. Please log in again.';
  } else if (error.response?.status === 403) {
    return 'Access forbidden. You do not have permission to perform this action.';
  } else if (error.response?.status === 404) {
    return 'Resource not found.';
  } else if (error.response?.status === 409) {
    return 'Email already exists. Please use a different email or try logging in.';
  } else if (error.response?.status >= 500) {
    return 'Server error. Please try again later.';
  } else {
    return error.message || 'An unexpected error occurred.';
  }
};

// Export the axios instance for direct use if needed
export default api;
